package pages;import org.openqa.selenium.WebElement;import org.openqa.selenium.support.FindBy;import org.openqa.selenium.support.PageFactory;import java.util.List;/** * @author Ivan Yushin */public class MainPage extends BasePage {    public MainPage(){        findXpath("//*[text()=' ок ']").click();    }    @FindBy(xpath = "//div[@class=\"row text-center\"]//div//a")    private List<WebElement> categories;    @FindBy(xpath = "//*[@href=\"/products/private_person/auto/index.wbp\"]")    private WebElement openCarInsurance;    @FindBy(xpath = "//*[@href=\"/products/private_person/tour/index.wbp\"]")    private WebElement openTourInsurance;    public void printAllCategoriesOfInsurance(){        categories.stream().forEach(System.out::println);    }    public void getCarInsurance(){        openCarInsurance.click();    }    public void getTourInsurance(){        openTourInsurance.click();    }}