package pages;import org.openqa.selenium.WebElement;import org.openqa.selenium.support.FindBy;import org.openqa.selenium.support.PageFactory;import java.util.List;/** * @author Ivan Yushin */public class MainPage extends BasePage {    @FindBy(xpath = "//ol[contains(@class,'rgs-menu pull-left')]")    private List<WebElement> insuranceTopCategories;    @FindBy(xpath = "//div[@class='row text-center']//div//a")    private List<WebElement> categoriesMiddle;    @FindBy(xpath = "//*[@href=\"/products/private_person/auto/index.wbp\"]")    private WebElement openCarInsurance;    @FindBy(xpath = "//*[@href=\"/products/private_person/tour/index.wbp\"]")    private WebElement openTourInsurance;    public MainPage(){        findXpath("//*[text()=' ок ']").click();    }    public void printAllCategoriesInTopBar(){        insuranceTopCategories.stream().map(WebElement::getText).forEach(System.out::println);    }    public void printAllCategoriesOfInsurance(){        categoriesMiddle.stream().map(WebElement::getText).forEach(System.out::println);    }    public void getCarInsurance(){        openCarInsurance.click();    }    public void getTourInsurance(){        openTourInsurance.click();    }}